update.foreach:
UPDATE dtb_order
cash_id = #{item.cashId,jdbcType=VARCHAR}, cash_discount_price = #{item.cashDiscountPrice,jdbcType=VARCHAR}, cash_product_price = #{item.cashProductPrice,jdbcType=VARCHAR}, cash_contail_tax_product_price = #{item.cashContailTaxProductPrice,jdbcType=VARCHAR}, updated_user_id = #{item.updatedUserId,jdbcType=VARCHAR}, updated_at = #{item.updatedAt,jdbcType=TIMESTAMP}, version = #{item.version,jdbcType=SMALLINT} + 1, tsc_status = #{item.tscStatus,jdbcType=VARCHAR}
WHERE order_id = #{item.orderId,jdbcType=VARCHAR} and version = #{item.version,jdbcType=SMALLINT}
