mapper.select:
  select margin_rate as marginRate
  from mtb_maker_margin as mmm,
	   mtb_fabric as mfa
  where mmm.maker_code = mfa.maker_code
        and mfa.fabric_no = #{arg0}
        and mfa.order_pattern = 'PO'

  select margin_rate as marginRate
  from mtb_maker_margin as mmm,
	   mtb_fabric as mfa
  where mmm.maker_code = mfa.maker_code
        and mfa.fabric_no = #{arg0}
        and mfa.order_pattern = 'CO'

select COALESCE(price, 0) as moapPrice,
	   COALESCE(double_price, 0) as moapDoublePrice,
	   option_branch_code as moapOptionBranchCode
from mtb_option_accessory_price
where factory_code = #{arg0}
  and item_code = #{arg1}
  and sub_item_code = #{arg2}
  and option_code = #{arg3}

select COALESCE(wage, 0) as mowWage,
	   option_branch_code as mowOptionBranchCode
from mtb_option_wage
where factory_code = #{arg0}
  and item_code = #{arg1}
  and sub_item_code = #{arg2}
  and option_code = #{arg3}
