mapper.sql:
WHERE
dtb_order.order_pattern = #{condition.category}
AND order_id LIKE '%${condition.orderId}%'
AND cust_cd LIKE '%${condition.custCd}%'
AND (tsc_status = '${condition.tscStatus}' OR maker_factory_status = '${condition.tscStatus}')
AND cust_staff LIKE '%${condition.storeStaffNm}%'
AND product_item = '${condition.productItem}'
AND product_item = '01' AND product_item_displaycode = '${condition.productItem}'
AND product_fabric_no LIKE '%${condition.productFabricNo}%'
AND fablic_importer = '${condition.fablicImporter}'
AND product_orderd_date >= #{condition.productOrderdDate2From} AND (tsc_status = 'T2' OR tsc_status = 'T3' OR tsc_status = 'T4' OR tsc_status = 'T5' OR tsc_status = 'T6' OR tsc_status = 'T7' )
AND product_orderd_date <= #{condition.productOrderdDate2To} AND (tsc_status = 'T2' OR tsc_status = 'T3' OR tsc_status = 'T4' OR tsc_status = 'T5' OR tsc_status = 'T6' OR tsc_status = 'T7' )
AND cust_shop_delivery_date >= #{condition.custShopDeliveryDate2From}
AND cust_shop_delivery_date <= #{condition.custShopDeliveryDate2To}
AND cust_deliver_date >= #{condition.custDeliverDate2From}
AND cust_deliver_date <= #{condition.custDeliverDate2To}
AND dtb_order.updated_at >= #{condition.updatedAt2From}
AND dtb_order.updated_at <= #{condition.updatedAt2To}
AND (tsc_status = 'T2' OR tsc_status = 'T3' OR tsc_status = 'T4' OR tsc_status = 'T5' OR tsc_status = 'T6' OR tsc_status = 'T7' ) AND product_maker_code = #{condition.belongCode}
AND (maker_factory_status = 'F1' OR maker_factory_status = 'F2') AND product_factory_cd = #{condition.belongCode}
AND (maker_factory_status = 'F1' OR maker_factory_status = 'F2')
AND is_cancelled = '0'
AND tsc_status = 'T2' AND (cash_id is null or cash_id = '')
AND tsc_status = 'T3'
AND tsc_status = 'T4'
AND send2factory_status = '4'
AND shop_code = #{condition.belongCode}
AND shop_code = #{condition.belongCode} AND (tsc_status = 'T0' OR tsc_status = 'T1')
AND (tsc_status = 'T2' OR tsc_status = 'T3' OR tsc_status = 'T4' OR tsc_status = 'T5' OR tsc_status = 'T6' OR tsc_status = 'T7' )
AND (shop_code = #{condition.belongCode} AND (tsc_status = 'T0' OR tsc_status = 'T1') OR (tsc_status = 'T2' OR tsc_status = 'T3' OR tsc_status = 'T4' OR tsc_status = 'T5' OR tsc_status = 'T6' OR tsc_status = 'T7' ))

mapper.update:
UPDATE dtb_order
tsc_status = #{arg1}, updated_user_id = #{arg2}, updated_at = #{arg3}, version = #{arg4} + 1
WHERE order_id = #{arg0} and version = #{arg4}

UPDATE dtb_order
fabric_used_mount = #{arg1}, shipping_date = #{arg2}, loading_date = #{arg3}, updated_user_id = #{arg4}, updated_at = #{arg5}, version = #{arg6} + 1
WHERE order_id = #{arg0} and version = #{arg6}

UPDATE dtb_order
fabric_used_mount = #{arg1}, shipping_date = #{arg2}, loading_date = #{arg3}, updated_user_id = #{arg4}, updated_at = #{arg5}, version = #{arg6} + 1, shipping_transmit_status = #{arg7}
WHERE order_id = #{arg0} and version = #{arg6}

UPDATE dtb_order
fabric_used_mount = #{arg1}, shipping_date = #{arg2}, loading_date = #{arg3}, maker_factory_status = #{arg4}, updated_user_id = #{arg5}, updated_at = #{arg6}, version = #{arg7} + 1
WHERE order_id = #{arg0} and version = #{arg7}

UPDATE dtb_order
fabric_used_mount = #{arg1}, shipping_date = #{arg2}, loading_date = #{arg3}, maker_factory_status = #{arg4}, updated_user_id = #{arg5}, updated_at = #{arg6}, version = #{arg7} + 1, shipping_transmit_status = #{arg8}
WHERE order_id = #{arg0} and version = #{arg7}

UPDATE dtb_order
ws_price = #{arg1}, updated_user_id = #{arg2}, updated_at = #{arg3}, version = #{arg4} + 1
WHERE order_id = #{arg0} and version = #{arg4}

UPDATE
dtb_order

	ws_price = #{arg1},
	updated_user_id = #{arg2},
	updated_at = #{arg3},
	version = #{arg4} + 1,
	schedule_data_transmit_status = #{arg5}

WHERE
	order_id = #{arg0}
	and version = #{arg4}

UPDATE mtb_stock
actual_stock = #{arg1}, updated_user_id = #{arg2}, updated_at = #{arg3}
WHERE fabric_no = #{arg0} and order_pattern = #{arg4}
